mapper.select:
SELECT count(*) FROM (SELECT jwj.jwid FROM jw_web_jwid AS jwj /* */ WHERE 1=1
UNION SELECT jwj.jwid FROM jw_web_jwid AS jwj,jw_system_user_jwid AS jsuj WHERE jwj.jwid = jsuj.jwid AND jsuj.user_id = #{query.createBy,jdbcType=VARCHAR}
) AS X

SELECT x.* FROM (SELECT jwj.id /* */ ,jwj.jwid /* 公众号 */ ,jwj.name /* 名称 */ ,jwj.application_type /* 应用类型 */ ,jwj.qrcodeimg /* 二维码 */ ,jwj.create_by /* 创建人 */ ,jwj.create_time /* 创建时间 */ ,jwj.weixin_number /* 微信号 */ ,jwj.weixin_appid /* 微信AppId */ ,jwj.weixin_appsecret /* 微信AppSecret */ ,jwj.account_type /* 公众号类型 */ ,jwj.auth_status /* 是否认证 0不认证 1认证 */ ,jwj.access_token /* AccessToken */ ,jwj.token_gettime /* token获取时间 */ ,jwj.auth_type /*类型：1手动授权，2扫码授权*/ ,jwj.business_info /* 功能的开通状况：1代表已开通*/ ,jwj.func_info /* 公众号授权给开发者的权限集*/ ,jwj.nick_name /* 授权方昵称*/ ,jwj.headimgurl /* 授权方头像*/ ,jwj.authorization_info /* 授权信息*/ ,jwj.authorizer_refresh_token /* 刷新token*/ ,jwj.token /* 令牌 */ ,jwj.authorization_status /* 授权状态 */ FROM jw_web_jwid AS jwj /* */ WHERE 1=1
UNION SELECT jwj.id /* */ ,jwj.jwid /* 公众号 */ ,jwj.name /* 名称 */ ,jwj.application_type /* 应用类型 */ ,jwj.qrcodeimg /* 二维码 */ ,jwj.create_by /* 创建人 */ ,jwj.create_time /* 创建时间 */ ,jwj.weixin_number /* 微信号 */ ,jwj.weixin_appid /* 微信AppId */ ,jwj.weixin_appsecret /* 微信AppSecret */ ,jwj.account_type /* 公众号类型 */ ,jwj.auth_status /* 是否认证 0不认证 1认证 */ ,jwj.access_token /* AccessToken */ ,jwj.token_gettime /* token获取时间 */ ,jwj.auth_type /*类型：1手动授权，2扫码授权*/ ,jwj.business_info /* 功能的开通状况：1代表已开通*/ ,jwj.func_info /* 公众号授权给开发者的权限集*/ ,jwj.nick_name /* 授权方昵称*/ ,jwj.headimgurl /* 授权方头像*/ ,jwj.authorization_info /* 授权信息*/ ,jwj.authorizer_refresh_token /* 刷新token*/ ,jwj.token /* 令牌 */ ,jwj.authorization_status /* 授权状态 */ FROM jw_web_jwid AS jwj,jw_system_user_jwid AS jsuj WHERE jwj.jwid = jsuj.jwid AND jsuj.user_id = #{query.createBy,jdbcType=VARCHAR}
) AS x LIMIT #{startRow} /* 开始序号 */ ,#{pageSize} /* 每页显示条数 */

mapper.update:
UPDATE jw_system_user_jwid SET
	jwid =#{newJwid}
WHERE jwid=#{jwid}

UPDATE jw_web_jwid SET
	jwid =#{newJwid}
WHERE jwid=#{jwid}

mapper.select:
SELECT count(*)  FROM  weixin_gzuser      AS wz      /*  */ 
 WHERE 1=1
 and wz.jwid = #{jwid}
 and wz.subscribe ='1'

	AND LEFT(wz.create_time,10) = #{refDate}

SELECT count(*)  FROM  weixin_receivetext      AS wr      /*  */ 
 WHERE 1=1
 and wr.jwid = #{jwid}

	AND DATE_FORMAT(wr.create_time, '%Y-%m-%d') = #{refDate}

		SELECT 
                 jwj.*                           
		FROM   	 jw_web_jwid      AS jwj      /*  */ 
		WHERE 
			     jwj.create_by = #{systemUserid}

	  		 		/* 公众号 */
			    	AND jwj.jwid =  #{jwid}

		LIMIT 1
