mapper.update:
  UPDATE   weixin_menu  	/* 微信菜单表 */


	    		 		/* 序号 */ 
    		 			id = #{id,jdbcType=VARCHAR},


	    		 		/* 父id */ 
    		 			father_id = #{fatherId,jdbcType=VARCHAR},


	    		 		/* 菜单KEY */ 
    		 			menu_key = #{menuKey,jdbcType=VARCHAR},


	    		 		/* 菜单类型 */ 
    		 			menu_type = #{menuType,jdbcType=VARCHAR},


	    		 		/* 菜单名称 */ 
    		 			menu_name = #{menuName,jdbcType=VARCHAR},


	    		 		/* 网页链接 */ 
    		 			url = #{url,jdbcType=VARCHAR},


	    		 		/* 相应消息类型 */ 
    		 			msgtype = #{msgtype,jdbcType=VARCHAR},


	    		 		/* 菜单位置 */ 
    		 			orders = #{orders,jdbcType=VARCHAR},


	    		 		/* 关联素材id */ 
    		 			template_id = #{templateId,jdbcType=VARCHAR},


	    		 		/* 公众号原始id */ 
    		 			jwid = #{jwid,jdbcType=VARCHAR},


	    		 		/* 小程序appid */ 
    		 			miniprogram_appid = #{miniprogramAppid,jdbcType=VARCHAR},


	    		 		/* 小程序页面路径 */ 
    		 			miniprogram_pagepath = #{miniprogramPagepath,jdbcType=VARCHAR},


	    		 		/* 创建人登录名称 */ 
    		 			create_by = #{createBy,jdbcType=VARCHAR},


	    		 		/* 创建时间 */ 
    		 			create_time = #{createTime,jdbcType=TIMESTAMP},


	    		 		/* 修改人登录名称 */ 
    		 			update_by = #{updateBy,jdbcType=VARCHAR},


	    		 		/* 修改时间 */ 
    		 			update_time = #{updateTime,jdbcType=TIMESTAMP},


			WHERE
  	 		 		id = #{id}		/* 序号 */

mapper.select:
SELECT * 
FROM weixin_menu
WHERE orders=#{orders} AND jwid =#{jwid}

SELECT count(*)
FROM weixin_menu
WHERE father_id =#{fatherId}

SELECT wm.id /* 序号 */ ,wm.father_id /* 父id */ ,wm.menu_key /* 菜单KEY */ ,wm.menu_type /* 菜单类型 */ ,wm.menu_name /* 菜单名称 */ ,wm.url /* 网页链接 */ ,wm.msgtype /* 相应消息类型 */ ,wm.orders /* 菜单位置 */ ,wm.template_id /* 关联素材id */ ,wm.jwid /* 公众号原始id */ ,wm.miniprogram_appid /* 小程序appid */ ,wm.miniprogram_pagepath /* 小程序页面路径 */ ,wm.create_by /* 创建人登录名称 */ ,wm.create_time /* 创建时间 */ ,wm.update_by /* 修改人登录名称 */ ,wm.update_time /* 修改时间 */ FROM weixin_menu AS wm /* 微信菜单表 */ WHERE 1=1
AND jwid=#{jwid}
AND father_id=#{fatherId}
AND (father_id='' OR father_id is null)
