mapper.update:
  UPDATE   weixin_receivetext  	/* 文本消息存储实体类 */


	    		 		/* 消息内容 */ 
    		 			content = #{content,jdbcType=VARCHAR},


	    		 		/* 创建日期 */ 
    		 			create_time = #{createTime,jdbcType=TIMESTAMP},


	    		 		/* 发送方帐号（OpenId） */ 
    		 			from_user_name = #{fromUserName,jdbcType=VARCHAR},


	    		 		/* 消息ID */ 
    		 			msg_id = #{msgId,jdbcType=VARCHAR},


	    		 		/* 消息类型(text:文本消息,news:图文消息,voice:音频消息,video:视频消息,image,图片消息)消息类型 */ 
    		 			msg_type = #{msgType,jdbcType=VARCHAR},


	    		 		/* JWID */ 
    		 			to_user_name = #{toUserName,jdbcType=VARCHAR},


	    		 		/* 多媒体ID */ 
    		 			media_id = #{mediaId,jdbcType=VARCHAR},


			WHERE
  	 		 		id = #{id}		/* 序号 */

mapper.select:
SELECT chatten.id as id /*回复消息id*/ , chatten.content as content/* 消息内容 */ , chatten.create_time as time/* 创建日期 */ , chatten.type as type , chatten.msgtype as msgtype , chatten.fromUserName as fromUserName , chatten.sendStauts as sendStauts FROM ( SELECT * FROM ( SELECT wr.id , wr.content /* 消息内容 */ , wr.create_time /* 创建日期 */ , 1 AS type , wr.msg_type AS msgtype , wr.from_user_name AS fromUserName , 1 AS sendStauts FROM weixin_receivetext AS wr /* 文本消息存储实体类 */ WHERE wr.from_user_name = #{fromUserName} AND wr.to_user_name = #{toUserName}
UNION ALL SELECT wrm.id /*回复消息id*/ , wrm.content /* 消息内容 */ , wrm.create_time /* 创建日期 */ , 2 AS type , wrm.msgtype , wrm.from_username AS fromUserName , wrm.send_stauts AS sendStauts FROM weixin_recept_msg AS wrm /* 文本消息存储实体类 */ WHERE wrm.to_username = #{fromUserName} AND wrm.jwid = #{toUserName} ) chatall WHERE 1=1
AND DATE_FORMAT(chatall.create_time,'%Y-%m-%d %H:%i:%s') > #{inRecentTime}
ORDER BY chatall.create_time DESC LIMIT 0,10 ) chatten ORDER BY chatten.create_time LIMIT 0,10

SELECT chatten.content as content/* 消息内容 */ , chatten.create_time as time/* 创建日期 */ , chatten.type as type , chatten.msgtype as msgtype , chatten.fromUserName as fromUserName FROM ( SELECT * FROM ( SELECT wr.content /* 消息内容 */ , wr.create_time /* 创建日期 */ , 1 AS type , wr.msg_type AS msgtype , wr.from_user_name AS fromUserName FROM weixin_receivetext AS wr /* 文本消息存储实体类 */ WHERE wr.from_user_name = #{fromUserName} AND wr.to_user_name = #{toUserName}
UNION ALL SELECT wrm.content /* 消息内容 */ , wrm.create_time /* 创建日期 */ , 2 AS type , wrm.msgtype , wrm.from_username AS fromUserName FROM weixin_recept_msg AS wrm /* 文本消息存储实体类 */ WHERE wrm.to_username = #{fromUserName} AND wrm.jwid = #{toUserName} ) chatall WHERE 1=1
AND DATE_FORMAT(chatall.create_time,'%Y-%m-%d %H:%i:%s') < #{firstRecordTime}
ORDER BY chatall.create_time DESC LIMIT 0,10 ) chatten ORDER BY chatten.create_time LIMIT 0,10
